import React, { useState } from 'react';
import styled from "styled-components";

const InputWrapper = styled.div`
  width: 100%;
  margin: 8px 0;
`;

const FormContainer = styled.div`
  padding: 20px;
  color: white;
`;

const StyledSelect = styled.select`
  display: block !important;
  width: 100% !important;
  padding: 12px !important;
  box-sizing: border-box !important;
  background-color: #f8f8f8;
`;

const StyledInput = styled.input`
  display: block !important;
  width: 100% !important;
  padding: 12px !important;
  box-sizing: border-box !important;
  background-color: #f8f8f8;
`;

const StyledButton = styled.input`
  height: 45px;
  margin: 10px;
  border: none;
  background-color: #66cc99;
  color: white;
  border-radius: 8px;
  font-size: 20px;
  cursor: pointer;
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const ButtonContainer = styled.div`
display: flex;
justify-content: flex-start;
width: 100%;
`;


const cities = ["São Paulo", "Rio de Janeiro", "Brasília", "Salvador", "Manaus", "Recife", 
"Curitiba", "Fortaleza", "Porto Alegre", "Natal", "Florianópolis", "Belém", "Goiânia", "Maceió", 
"Campo Grande", "Cuiabá", "João Pessoa", "Aracaju", "Teresina", "Vitória", "São Luís", "Palmas", 
"Boa Vista", "Porto Velho"];
function FormViagem({ addViagem }) {
    const [origem, setOrigem] = useState('');
    const [destino, setDestino] = useState('');
    const [duracao, setDuracao] = useState(1);
    const [deadline, setDeadline] = useState('');

    const handleAddViagem = (event) => {
        event.preventDefault();
        addViagem(origem, destino, duracao, deadline);
        setOrigem('');
        setDestino('');
        setDuracao(1);
        setDeadline('');
    }

    const handleCancel = () => {
        setOrigem('');
        setDestino('');
        setDuracao(1);
        setDeadline('');
    }



    return (
        <FormContainer>
            <StyledForm onSubmit={handleAddViagem}>
                <label>
                    Origem:
                    <StyledSelect value={origem} onChange={e => setOrigem(e.target.value)}>
                        <option value="">Selecione uma cidade</option>
                        {cities.map((city, index) => (
                            <option key={index} value={city}>{city}</option>
                        ))}
                    </StyledSelect>
                </label>
                <label>
                    Destino:
                    <StyledSelect value={destino} onChange={e => setDestino(e.target.value)}>
                        <option value="">Selecione uma cidade</option>
                        {cities.map((city, index) => (
                            <option key={index} value={city}>{city}</option>
                        ))}
                    </StyledSelect>
                </label>
                <label>
                    Duração (dias):
                    <StyledInput type="number" min="1" value={duracao} onChange={e => setDuracao(e.target.value)} />
                </label>
                <label>
                    Deadline:
                    <StyledInput type="date" value={deadline} onChange={e => setDeadline(e.target.value)} />
                </label>
                <ButtonContainer>
                <StyledButton type="submit" value="Adicionar" />  
                <StyledButton type="button" onClick={handleCancel} value="Cancelar" />
                </ButtonContainer>

            </StyledForm>
        </FormContainer>
    );
}

export default FormViagem;
