import { VectorMap } from '@south-paw/react-vector-maps';
import React, { useEffect, useState } from 'react';
import BrMap from './map/brMap';
import { Container, Map, MapContainer } from './styles';
import Navbar from './components/Navbar';
import TypingText from './components/Message';
import Graph from './graph/Graph';
import ReasultModal from './components/ResultModal';

import Home from './components/Home';
import { Routes, Route } from 'react-router-dom';
import About from './components/About';
import FormViagem from './components/FormViagem';
import Vector from './Vector/Vector';
import Viagem from './Vector/Viagem';
const App = () => {

  const [focused, setFocused] = useState('None');
  const [output, setOutput] = useState('None');
  const [starting, setStarting] = useState('None');
  const [destiny, setDestiny] = useState('None');
  const [modal, setModal] = useState(false);

  const vector = new Vector();
  const [lastViagem, setLastViagem] = useState(null);


  const initialViagens = [
    new Viagem("São Paulo", "Rio de Janeiro", 6, "02-06-2023"),
    new Viagem("Brasília", "Salvador", 2, "05-06-2023"),
    new Viagem("Manaus", "Recife", 4, "08-06-2023"),
    new Viagem("Curitiba", "Fortaleza", 3, "10-06-2023"),
    new Viagem("Porto Alegre", "Natal", 4, "15-06-2023"),
    new Viagem("Florianópolis", "Belém", 5, "20-06-2023"),
    new Viagem("Goiânia", "Maceió", 3, "25-06-2023")
  ];
  const [viagens, setViagens] = useState(initialViagens);

  const addViagem = (origem, destino, duracao, deadline) => {
    const viagem = new Viagem(origem, destino, duracao, deadline);
    setViagens(oldViagens => [...oldViagens, viagem]);
    setLastViagem(viagem);
  };

  const layerProps = {
    onFocus: ({ target }) => setFocused(target.attributes.name.value),
    onClick: ({ target }) => {
      const id = target.attributes.id.value;
      if (starting === 'None') {
        setStarting(id);
      } else if (destiny === 'None') {
        setDestiny(id);
      } else {
        setStarting(id);
        setDestiny('None');
      }
    }
  };

  const graph = new Graph();

  useEffect(() => {

    console.log(`starting: ${starting} destiny: ${destiny}`);


    if (starting === 'None' && destiny === 'None') {
      setOutput('Selecione os estados');
      return;
    }
    if (starting != 'None' && destiny != 'None') {

      const resultado = graph.shortestPath(starting, destiny);
      setOutput(resultado);
      setModal(true);
    }

  }, [starting, destiny]);

  useEffect(() => {
    const vector = new Vector();
    vector.viagens = viagens;
    vector.scheduleToMinimizeLateness();

  }, [viagens]);

  useEffect(() => {
    if (lastViagem !== null) {
      setModal(true);
    }
  }, [lastViagem]);
  return (
    <Container>
      {modal && <ReasultModal
        visible={modal}
        setVisible={setModal}
        output={output}
      />}
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/sobre" element={<About />} />
        <Route path="/minimizing" element={<FormViagem addViagem={addViagem}
        />}

        />
        <Route path="/dijkstra" element={
          <MapContainer>
            <Map>
              <TypingText message='Escolha dois estados que o algoritmo de Dijkstra apresentará o melhor caminho para o Zé Inácio! ' shouldDelete={false} />
              <VectorMap {...BrMap} layerProps={layerProps} checkedLayers={[starting, destiny]} />
            </Map>
          </MapContainer>
        } />
      </Routes>
    </Container>
  );
}

export default App;
