import React from 'react';
import TypingText from '../Message';
import { Body, Container, Footer, ModalBackground, TitleCloseBtn } from './styles';

const ReasultModal = ({ visible, setVisible, output,lastViagem }) => {
    let message = '';
    if (lastViagem) {
        message = `Diga ao Zé Inácio que a próxima viagem é de ${lastViagem.origem} para ${lastViagem.destino}, com uma duração de ${lastViagem.duracao} e um prazo de ${lastViagem.deadline}.`;
    } else if (output) {
        message = `Diga ao Zé Inácio que o melhor caminho é passando pelas capitais de ${output.path.join(', ')} nessa ordem. Esta viagem terá aproximadamente ${output.distance} quilômetros.`;
    }

    return (
        <ModalBackground>
            <Container>
                <TitleCloseBtn>
                    <button
                        onClick={() => {
                            setVisible(false);
                        }}
                    >
                        X
                    </button>
                </TitleCloseBtn>

                <Body>

                    <TypingText
                        message={`Diga ao Zé Inácio que o melhor caminho é passando pelas capitais de ${output.path.join(', ')} nessa ordem. Esta viagem terá aproximadamente ${output.distance} quilômetros.`}
                        shouldDelete={false}
                    />

                </Body>
                <Footer>
                    <button
                        onClick={() => {
                            setVisible(false);
                        }}
                        id="cancelBtn"
                    >
                        Fechar
                    </button>
                </Footer>
            </Container>
        </ModalBackground>
    );
}
export default ReasultModal;
